generated.css:
    body{
      text-align: center;
      overflow: hidden;
      font-family: 'Lato', sans-serif;
      user-select: none;
      background: url("39895b98f0d48ddd5ceee1893aeece0f.svg") center; 
    }
    li{
      list-style-type: none;
    }
    ul{
      text-align: left;
      width: 300px;
      margin: 0 auto;
    }
    .niveau{
      background: #724C33;
      color: #AF7643;
      border-radius: 10px;
      padding: 5px;
      font-size: 40px;
      font-weight: 400;
      width: 300px;
      margin: 20px auto 25px auto;
      box-shadow: inset -2px -2px 0px 2px #7C5337;
    }
    #card-container{
      width: 300px;
      height: 350px;
      margin: 0 auto;
      position: relative;
    }
    #card-tracker{
      height: 350px;
      width: 500px;
      left: 0;   
      right: 0;
      position: absolute;
      text-align: center;
      z-index: 2;
      background: red;
      opacity: 0;
      transform: translateX(-100px);
    }
    .card-visible{
      height: 350px;
      width: 300px;
      left: 0;
      right: 0;
      position: absolute;
      transition: 0.1s;
      border-radius: 30px;
      box-shadow: 6px 6px 29px -9px rgba(0,0,0,0.65);
    }
    #card{
      height: 280px;
      width: 300px;
      left: 0;
      right: 0;
      position: absolute;
      color: white; 
      text-align: center;
      transition: 0.1s;
      opacity: 1;
      line-height: 250px;
      border-radius: 30px 30px 0 0;
      background: url("e40904416d583be2cb57ca10d5414b1e.svg");
      

    }
    #feedback{
      color: white;
      position: absolute;
      width: 280px;
      left: 10px;
      line-height: 1.4;
      top: 135px;
      filter: none;
      font-size: 20px;
      /* Le placement pour centrer est Ã  refaire!*/
    }
    #name{
      position: absolute;
      top: 0;
      display: inline-block;
      height: 30px;
      width: auto;
      margin-left: auto;
      margin-right: auto;
      left: 0;
      right: 0;
      line-height: 1;
      text-align: center;
    }
    #room_name{
      padding: 8px 35px 10px 35px;
      background: #333333;
      display: inline-block;
      margin: 0 auto;
      clip-path: polygon(0 0, 100% 0, 85% 100%, 15% 100%);
    }
    #room_img{
      background: url("59d074125cf5a06a61465ff611789ea6.svg") center no-repeat;
      width: 220px;
      height: 220px;
      margin: 0 auto;
      margin-top: 40px;
    }
    #room_desc{
      background: #D8D3AF;
      height: 46px;
      line-height: 1;
      margin-top: 20px;
      padding: 12px;
      line-height: 1.4;
      
      border-radius: 0 0 30px 30px;
      color: #1A1A1A;
    }
    .yes {
      -moz-transform: rotate(10deg) translateX(50px);
      -webkit-transform: rotate(10deg) translateX(50px);
      -o-transform: rotate(10deg) translateX(50px);
      -ms-transform: rotate(10deg) translateX(50px);
      transform: rotate(10deg) translateX(50px);
    }
    .yes #btn_yes-text {
      opacity: 1;
      transition: .5s;
    }

    .yesFade {
      opacity: 0;
      transition: .5s;
    }

    .no {
      -moz-transform: rotate(-10deg) translateX(-50px);
    -webkit-transform: rotate(-10deg) translateX(-50px);
    -o-transform: rotate(-10deg) translateX(-50px);
    -ms-transform: rotate(-10deg) translateX(-20px);
    transform: rotate(-10deg) translateX(-50px);
    }
    .no #btn_no-text {
      opacity: 1;
    }


    .noFade {
      opacity: 0;
    }
    #btn_no {
      background: url("ddbcf682c06d1c80b1b0499be65ef159.svg") no-repeat center;
      width: 80px;
      height: 80px;
      position: absolute;
      left: -30px;
      bottom: 30px;
      line-height: 1;
      filter: drop-shadow( 3px 3px 4px rgba(0,0,0,0.3));
    }
    #btn_no-text{
      position: absolute;
      bottom: -45px;
      left: 45px;
      width: 300px;
      text-align: left;
      color: white;
      font-size: 22px;
      opacity: 0;
      
    }
    #btn_yes {
      background: url("d6939278153ea1a9e5e20047a11c31e2.svg") no-repeat center;
      width: 80px;
      height: 80px;
      position: absolute;
      right: -30px;
      bottom: 30px;
      line-height: 1;
      filter: drop-shadow( 3px 3px 4px rgba(0,0,0,0.3));
    }
    #btn_yes-text{
      position: absolute;
      bottom: -45px;
      right: 45px;
      width: 300px;
      text-align: right;
      color: white;
      font-size: 22px;
      opacity: 0;
      
    }
    #room_type{margin: 0; padding: 0; line-height: 1;}
    h3{margin: 10px 0; padding: 0; line-height: 0;}

    .feedback-message{
      filter: brightness(.25);
    }
    @keyframes fadeIn {
      0% {opacity: 0; transform: scale(0);}
      100% {opacity: 1; transform: scale(1);}
    }
