    body{
      text-align: center;
      overflow: hidden;
      font-family: 'Slabo 27px', serif;
    }
    li{
      list-style-type: none;
    }
    ul{
      text-align: left;
      width: 300px;
      margin: 0 auto;
    }
    #card-container{
      width: 300px;
      height: 300px;
      margin: 0 auto;
      position: relative;
    }
    #card-tracker{
      height: 300px;
      width: 500px;
      left: 0;   
      right: 0;
      position: absolute;
      text-align: center;
      z-index: 2;
      background: red;
      opacity: 0;
      transform: translateX(-100px);
    }
    .card-visible{
      height: 300px;
      width: 300px;
      left: 0;
      right: 0;
      position: absolute;
      color: white; 
      text-align: center;
      transition: 0.1s;
      opacity: 1;
      line-height: 250px;
      border-radius: 30px;
      background: url("845bc018acac1986e047b6904fa67b66.svg");
    }
    #feedback{
      color: white;
      position: absolute;
      z-index: 5;
      width: 280px;
      left: 10px;
      line-height: 1.4;
      top: 135px;
      filter: none;
      /* Le placement pour centrer est Ã  refaire!*/
    }
    #room_img{
      background: url("35e2de55efce6747bb987a53a67e84e3.svg") center no-repeat;
      width: 220px;
      height: 220px;
      margin: 0 auto;
      margin-top: 50px;
    }
    .yes {
      -moz-transform: rotate(10deg) translateX(50px);
      -webkit-transform: rotate(10deg) translateX(50px);
      -o-transform: rotate(10deg) translateX(50px);
      -ms-transform: rotate(10deg) translateX(50px);
      transform: rotate(10deg) translateX(50px);
    }
    .yes #btn_yes-text {
      opacity: 1;
      transition: .5s;
    }

    .yesFade {
      opacity: 0;
      transition: .5s;
    }

    .no {
      -moz-transform: rotate(-10deg) translateX(-50px);
    -webkit-transform: rotate(-10deg) translateX(-50px);
    -o-transform: rotate(-10deg) translateX(-50px);
    -ms-transform: rotate(-10deg) translateX(-20px);
    transform: rotate(-10deg) translateX(-50px);
    }
    .no #btn_no-text {
      opacity: 1;
    }


    .noFade {
      opacity: 0;
    }
    #btn_no {
      background: url("97fb19066fb387b7db5c10c7b78ec113.svg") no-repeat center;
      width: 80px;
      height: 80px;
      position: absolute;
      left: -30px;
      bottom: 35px;
      line-height: 1;
    }
    #btn_no-text{
      position: absolute;
      bottom: -45px;
      left: 45px;
      width: 300px;
      text-align: left;
      color: white;
      font-size: 22px;
      opacity: 0;
      
    }
    #btn_yes {
      background: url("a56ed14cb19445fa7e69776a35c2e8da.svg") no-repeat center;
      width: 80px;
      height: 80px;
      position: absolute;
      right: -30px;
      bottom: 35px;
      line-height: 1;
    }
    #btn_yes-text{
      position: absolute;
      bottom: -45px;
      right: 45px;
      width: 300px;
      text-align: right;
      color: white;
      font-size: 22px;
      opacity: 0;
      
    }
